    private void btnSomarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSomarActionPerformed
        //converto o valor que chega da CAIXA TEXTO para inteiro e guardar o valor para o c√°lculo
        int x = Integer.parseInt(varX.getText()); 
        int y = Integer.parseInt(varY.getText());
        x = x + y;
        //inverto de inteiro para string para imprimir na caixa de texto novamente
        String resultado = String.valueOf(x);
        varResultado.setText(resultado);
    }//GEN-LAST:event_btnSomarActionPerformed

    private void btnLimparActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnLimparActionPerformed
        //Para limpar CAIXA DE TEXTO
      //  meuTextField.setText("");
      varX.setText("");
      varY.setText("");
      varResultado.setText("");
    }//GEN-LAST:event_btnLimparActionPerformed

    private void btnSubtrairActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSubtrairActionPerformed
        int x = Integer.parseInt(varX.getText()); 
        int y = Integer.parseInt(varY.getText());
        x = x - y;
        //inverto de inteiro para string para imprimir na caixa de texto novamente
        String resultado = String.valueOf(x);
        varResultado.setText(resultado);
    }//GEN-LAST:event_btnSubtrairActionPerformed

    private void btnMultiplicarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnMultiplicarActionPerformed
        
        int x = Integer.parseInt(varX.getText()); 
        int y = Integer.parseInt(varY.getText());
        x = x * y;
        //inverto de inteiro para string para imprimir na caixa de texto novamente
        String resultado = String.valueOf(x);
        varResultado.setText(resultado);
    }//GEN-LAST:event_btnMultiplicarActionPerformed

    private void btbDividirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btbDividirActionPerformed
        int x = Integer.parseInt(varX.getText()); 
        int y = Integer.parseInt(varY.getText());
        x = x / y;
        //inverto de inteiro para string para imprimir na caixa de texto novamente
        String resultado = String.valueOf(x);
        varResultado.setText(resultado);    }//GEN-LAST:event_btbDividirActionPerformed

