    static final float dolar = (float) 3.73;
    static final float remimbi = (float) 0.54;
    static final float coreano = (float) 0.0033;    
    private void btConverteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btConverteActionPerformed
        float valorEntrada = Float.parseFloat(cxEntrada.getText());
        float resultado = 0;
        
        if(opcDolar.isSelected() == true )//se estiver marcado - retorna o estado
            {
                resultado = valorEntrada / dolar;
                String mostrar = String.valueOf(resultado);
                cxSaida.setText(mostrar);
                opcDolar.setSelected(false);//estou desmarcando a checkboxx
            }
        if(opcCoreano.isSelected() == true )//se estiver marcado - retorna o estado
            {
                resultado = valorEntrada / coreano;
                String mostrar = String.valueOf(resultado);
                cxSaida.setText(mostrar);
                opcCoreano.setSelected(false);//estou desmarcando a checkboxx
            }
        if(opcRemimbi.isSelected() == true )//se estiver marcado - retorna o estado
            {
                resultado = valorEntrada / remimbi;
                String mostrar = String.valueOf(resultado);
                cxSaida.setText(mostrar);
                opcRemimbi.setSelected(false);//estou desmarcando a checkboxx
            }
    }//GEN-LAST:event_btConverteActionPerformed

    private void btLimparActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btLimparActionPerformed
     cxSaida.setText("");
     cxEntrada.setText("");
     opcRemimbi.setSelected(false);
     opcCoreano.setSelected(false);
     opcDolar.setSelected(false);
    }//GEN-LAST:event_btLimparActionPerformed

